import Button from "../../component/Button/Button";
import cerImg from "../../img/bmw-blac.jpeg";
import {Link} from "react-router-dom";
import './blog.scss'

import {Users} from "../../data/Users";
import List from "../../component/List/List";
import "../../component/List/list.scss"

import {isMobile} from 'react-device-detect';

import React, { useRef, useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";

// import "./styles.css";

// import required modules
import { Pagination } from "swiper";

function Blog() {
    const newObjIf = Users.map(props => (
        <SwiperSlide><List key={props.id} name={props.name} price={props.price}/></SwiperSlide>
    ))

    const pagination = {
        clickable: true,
        renderBullet: function (index, className) {
            return '<span class="' + className + '">' + (index + 1) + "</span>";
        },
    };

    return (
        <>
            <section>
                <h2>Blog page</h2>
                <div className="box">
                    <h2>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Non, optio.</h2>
                    <Button name="Hello"/>
                    <Button/>
                    <Button className="header__button" name='Open'/>
                    <Link to='/blog'>Blog</Link>
                </div>
                <div className="blog__swiper">
                    <Swiper
                        pagination={pagination}
                        modules={[Pagination]}
                        className="mySwiper">
                        {newObjIf}
                    </Swiper>
                </div>
            </section>
        </>
    )
}
export default Blog
