import React from "react";
import './TestInfo.scss'

export default function Blog({item}) {
    return (
        <div className="info__cart">
            <h2 className="info__title">{item.attributes.Title}</h2>
            <img src={`http://localhost:1337${item.attributes.img.data.attributes.url}`} alt=""/>
            <h4 className="info__title">{item.attributes.description}</h4>
            <p>category: {item.attributes.category}</p>
        </div>
    )
}







