    const {isLoading, data} = useFetch('https://rickandmortyapi.com/api/character?page=2')
    console.log(data?.results)

    // let RickAndMorty = data?.results.map(item => <CartRick key={item.id} item={item}/>)

    return isLoading ? <h1>...isLoading</h1> : (
        <>
            <section>
                <h2>RickAndMorty</h2>
                <div className="rick-search-row">
                    <input type="text"/><button>Search</button>
                </div>
                <div className="cards-out">{RickAndMorty}</div>
            </section>
        </>
    )
}
